							) or (
								power.ranking and
								translatedMessage("ranking_pos", player, power.ranking) or
								completed .. "/" .. power.maps
							)
						),
						player,
						170 + (index - page * 3) * 160,
						150,
						140,
						125,
						0x1c3a3e,
						0x193e46,
						1,
						true
					)
					images._count = images._count + 1
					images[images._count] = tfm.exec.addImage(
